            '${2:line_ids}', line_ids
        )
        lines.sort(key=lambda x:x.get('line_number'))

        if lines:
            sequence = lines[-1]['line_number'] + 10
        else:
            sequence = 10

        default_values.update({'line_number': sequence})

    return default_values

